from flask import Flask, render_templatefrom utils import get_post, get_posts, load_statsapp = Flask(__name__, static_url_path='')@app.errorhandler(404)def page_not_found(error):    return render_template('404.html'), 404@app.errorhandler(500)def server_error(error):    return render_template('500.html'), 500@app.route('/')def index():    return render_template('index.html')@app.route('/blog/')def blog_list():    posts = get_posts('blog')    return render_template('blog/list.html', posts=posts)@app.route('/blog/<year>/<file>/')def blog_detail(file, year):    post = get_post(file, 'blog', year)    return render_template('blog/detail.html', post=post)@app.route('/contact/')def contact():    return render_template('contact.html')@app.route('/stats/')def stats():    books = load_stats('books')    movies = load_stats('movies', 6)    music = load_stats('music', 6)    shows = load_stats('shows', 6)    return render_template('stats.html', books=books, movies=movies,                           music=music, shows=shows)if __name__ == '__main__':    app.run(host='0.0.0.0')